import { Typography } from "@material-ui/core";
import React, { useState,useEffect } from "react";
import { Link } from "react-router-dom";
import Header from "../../common/header/Header"
import "./Details.css";

const Details = (props) => {

    let dataMovie = null;

    const [selectedMovie,setSelectedMovie] = useState([]);

    function loadSelectedMovieData(){
        fetch(props.baseURL + "movies/" + props.match.params.id , {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
            //   "Cache-Control": "no-cache",
            },
            body: dataMovie,
          })  
          .then((response) => response.json())
          .then((response) => {
            setSelectedMovie(response);
        });
    }

    useEffect(() => {
        loadSelectedMovieData();
    },[]);

    return(
        <div>
            <Header/>
            <div className="backToHome">
                <Typography>
                    <Link to="/" underline="hover" className="backLink">&lt; Back to Home</Link>
                </Typography>
            </div>

            <div className="flex-container">
                <div className="left-container">
                    <img src={selectedMovie.poster_url} title={selectedMovie.title}/>
                </div>

                <div className="middle-container">

                    <Typography variant="headline" component="h2">{selectedMovie.title} </Typography>
                    <br/>
                    <Typography>
                        <span style="fontWeight: bold;">Genres: </span> {selectedMovie.genres.join(",")}
                    </Typography>
                </div>

                <div className="right-container">

                </div>

            </div>
        </div>
    )
}

export default Details;
