import React, { useLayoutEffect, useState } from "react";
import Header from "../../common/header/Header"
import "./Home.css"
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import { withStyles } from '@material-ui/core/styles';

const useStyles = theme => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
      backgroundColor: theme.palette.background.paper,
    },
    gridList: {
      flexWrap: 'nowrap',
      transform: 'translateZ(0)',
      width:'100%'
    },
    title: {
      color: theme.palette.primary.light,
    },
    titleBar: {
      background:
        'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
    },
  });

const Home = (props) => {
    const classes = props;
    const [movies,setUpcomingMoviesList] = useState([]);
    let dataUpcoming =null;
    function loadMoviesData(){
        fetch(props.baseURL + "movies?status=PUBLISHED", {
            method: "GET" ,
            headers: {
                "Cache-Control" : "no-cache",
                'Accept': 'application/json'
            },
            body: dataUpcoming,
        }).then((response) => response.json())
          .then((response) => {
            setUpcomingMoviesList(response.movies);
        });
    }

    useLayoutEffect(() => {
        loadMoviesData();
        console.log(movies)
    },[])

    return(
        <div>
            <Header/>
            <div className="upMovHeader">Upcoming Movies</div>
            <div className={classes.root}>
                <GridList className={classes.gridList} cols={6} cellHeight={250}>
                    {movies.map((mov) => (
                    <GridListTile key={mov.id}>
                        <img src={mov.poster_url} alt={mov.title} />
                        <GridListTileBar
                        title={mov.title}
                        classes={{
                            root: classes.titleBar,
                            title: classes.title,
                        }}
                        />
                    </GridListTile>
                    ))}
                </GridList>
            </div>
        </div>
    )
}

export default withStyles(useStyles)(Home);
