import React, { useEffect, useState } from "react";
import Header from "../../common/header/Header"
import "./Home.css"
import { GridListTile,GridList,GridListTileBar } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
      backgroundColor: theme.palette.background.paper,
    },
    gridList: {
      flexWrap: 'nowrap',
      // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
      transform: 'translateZ(0)',
    },
    title: {
      color: theme.palette.primary.light,
    },
    titleBar: {
      background:
        'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
    },
  }));

export default function Home () {

    const [movies,moviesList] = useState([]);

    function loadMoviesData(){
        fetch("http://localhost:8085/api/v1/movies?page=1&limit=6").then(input=>input.json()).then(data=>moviesList)
    }

    useEffect(() => {
        loadMoviesData();

    },[])
    const classes = useStyles();
    return(
        <div>
            <Header/>
            <div className="upMovHeader">Upcoming Movies</div>
            <div className={classes.root}>
                <GridList className={classes.gridList} cols={2.5}>
                    {movies.map((tile) => (
                    <GridListTile key={tile.img}>
                        <img src={tile.img} alt={tile.title} />
                        <GridListTileBar
                        title={tile.title}
                        classes={{
                            root: classes.titleBar,
                            title: classes.title,
                        }}
                        />
                    </GridListTile>
                    ))}
                </GridList>
            </div>
        </div>
    )
}
